  // const exercises1 = 10
  // const part2 = 'Using props to pass data'
  // const exercises2 = 7
  // const part3 = 'State of a component'
  // const exercises3 = 14
  const course = 'Half Stack application development'
  const part1 = {
    name: 'Fundamentals of React',
    exercises: 10
  }
  const part2 = {
    name: 'Using props to pass data',
    exercises: 7
  }
  const part3 = {
    name: 'State of a component',
    exercises: 14
  }
  return (
    <div>
      <Header course={course} />
       <Content part={part1.name} exercises={exercises1.exercises}  />
       <Content part={part2.name} exercises = {exercises2.exercises} />
       <Content part = {part3.name} exercises = {exercises3.exercises} />

       <Total total = {exercises1 + exercises2 + exercises3} />
    </div>
  )
}
const Header = (props) => {
  return (
    <>
    <h1>{props.course}</h1>
    </>

  )
}

const Part = (props) =>{
  return (
    <>
      <p>
      {props.part} {props.exercises}
      </p>
    </>
  )
}

const Content = (props) => {
  return(
    <>
    <Part part = {props.part} exercises = {props.exercises}/>
    </>
  )
}

const Total = (props) =>{
  return(
  <>
  <p>Number of exercises {props.total}</p>
  </>
  )
}

